module Microsoft.Research.Liquid.GaussSums

// implement the diagonal phase operator that computes the difference set 
// GateDiag <list of 0, 1> <qubits> 
let PhaseGateDiag (a:int[]) (qs:Qubits) = 
    let gate (a:int[]) =  
        Gate(
            Qubits  = qs.Length,
            Name    = sprintf "Diag(%A)" a,
            Help    = sprintf "Diagonal matrix corresponding to %A" a,
            Mat     = (
                let mat     = CSMat(a.Length ,true)
                for i in 0..a.Length-1 do   // diagonal phase matrix corresponding to array a
                    mat.r(i,i) <- (pown (-1.0) a.[i])
                mat),
            Draw    = "\\gate{{\rm diag}_L}"
            )
    (gate a).Run qs
    
// implement the diagonal phase operator 
// GateDiag <list of 0, 1> <qubits> 
let GaussSumGateDiag (a:double[]) (b:double[]) (qs:Qubits) = 
    let gate (a:double[]) (b:double[])=  
        Gate(
            Qubits  = qs.Length,
            Name    = sprintf "Diag(%A)" a,
            Help    = sprintf "Diagonal matrix corresponding to %A" a,
            Mat     = (
                let mat     = CSMat(a.Length ,true)
                for i in 0..a.Length-1 do   // diagonal phase matrix corresponding to arrays a and b 
                    mat.r(i,i) <-  a.[i]    // real part
                    mat.i(i,i) <- -b.[i]    // imaginary part; note that we compute \overline(\chi(D))
                mat),
            Draw    = "\\gate{{\rm diag}_G}"
            )
    (gate a b).Run qs


